mapper.select:
SELECT A.*, B.* FROM ( SELECT TT1.RNUM AS NO, TT1.PREVENT_SEQ AS preventSeq, TT1.title, TT1.contents, TT1.read_cnt as readCnt, TT1.img_src as imgSrc, TT1.reg_id as regId, -- 등록자 TT1.reg_dt as regDt, -- 등록일 TT1.mod_id as modId, DECODE( TO_CHAR(TT1.mod_dt, 'YYYYMMDD'), TO_CHAR(SYSDATE, 'YYYYMMDD'), TO_CHAR(TT1.mod_dt, 'HH24:MI'), TO_CHAR(TT1.mod_dt, 'YYYY/MM/DD') ) AS modDt FROM ( SELECT ROWNUM AS RNUM, T1.* FROM ( SELECT * FROM prevent WHERE 1=1
) T1 WHERE ROWNUM <= (#{pageSize} * (#{pageNo} - 1) + #{pageSize}) ) TT1 WHERE TT1.RNUM >= (#{pageSize} * (#{pageNo} - 1) + 1) ) A CROSS JOIN ( SELECT COUNT(*) totalCnt FROM prevent WHERE 1=1
) B
